from dataclasses import dataclass
from typing import Optional

from routeviews import peeringdb, types


@dataclass
class PeerRequest:
    """A possible BGP Peering between two routers. 

    - Generated by 'routeviews.peeringdb.Network.potential_peerings_with_network(other_network)'
    - Ingested by 'routeviews.ansible.CollectorConfig'.
    """
    my_network: 'peeringdb.Network'
    my_address: types.IPAddr
    your_network: 'peeringdb.Network'
    your_address: types.IPAddr
    exchange: Optional[str] = None  # Multihop peer requests have no exchange!
